mapper.select:
select * from s2_metric where 1=1
and type = #{type}
and ( id like CONCAT('%',#{key , jdbcType=VARCHAR},'%') or name like CONCAT('%',#{key , jdbcType=VARCHAR},'%') or biz_name like CONCAT('%',#{key , jdbcType=VARCHAR},'%') or description like CONCAT('%',#{key , jdbcType=VARCHAR},'%') )
and id like CONCAT('%',#{id , jdbcType=VARCHAR},'%')
and name like CONCAT('%',#{name , jdbcType=VARCHAR},'%')
and biz_name like CONCAT('%',#{bizName , jdbcType=VARCHAR},'%')
and sensitive_level = #{sensitiveLevel}
and model_id in
#{model}
